#pragma once

namespace caf {
namespace detail {

template <class T>
T gcd(T a, T b) {
  T r;
  while (b != 0) {
    r = a % b;
    a = b;
    b = r;
  }
  return a;
}

} // namespace detail
} // namespace caf


