#pragma once

#include <string>

namespace caf {
namespace io {
namespace network {

/// Identifies network IO operations, i.e., read or write.
enum class operation {
  read,
  write,
  propagate_error
};

inline std::string to_string(operation op) {
  return op == operation::read ? "read"
                               : (op == operation::write ? "write"
                                                         : "propagate_error");
}

} // namespace network
} // namespace io
} // namespace caf

