#include "caf/io/network/protocol.hpp"

namespace caf {
namespace io {
namespace network {

std::string to_string(const protocol& x) {
  std::string result;
  result += to_string(x.trans);
  result += "/";
  result += to_string(x.net);
  return result;
}

} // namespace network
} // namespace io
} // namespace caf

